     <script>
         jQuery(document).ready(function($) {
             // Direct selectors instead of storing parent references
             function initTabs() {
                 // Make sure the first tab is active and first content is visible
                 $('.tab_wrap .tab').first().addClass('active');
                 $('.content_wrap .box').hide();
                 $('.content_wrap .box').first().show();

                 // Attach event handlers to tabs
                 $('.tab_wrap .tab').each(function(i) {
                     // Click handler
                     $(this).on('click', function() {
                         if (!$(this).hasClass('active')) {
                             switchToTab(i);
                         }
                     });

                     // Hover handler
                     $(this).on('mouseenter', function() {
                         if (!$(this).hasClass('active')) {
                             switchToTab(i);
                         }
                     });
                 });
             }

             // Function to switch tabs
             function switchToTab(index) {
                 // Deactivate all tabs and hide all content
                 $('.tab_wrap .tab').removeClass('active');
                 $('.content_wrap .box').hide();

                 // Activate the clicked tab and show its content
                 $('.tab_wrap .tab').eq(index).addClass('active');
                 $('.content_wrap .box').eq(index).fadeIn(300);
             }

             // Initialize tabs when document is ready
             initTabs();
         });
     </script>
     <script>
         jQuery(document).ready(function($) {
             let url = $.parseJSON($('#playVideo').attr('data-settings')).background_video_link;
             let videoID = youtube_parser(url);
             let videoURL = "https://www.youtube.com/embed/" + videoID + "?autoplay=1&loop=1&playlist=" + videoID;
             $("#playVideo").on("click", function() {
                 if (jQuery('#lightbox').css('display') == "none") {
                     jQuery("#lightbox").fadeIn(1000);
                     jQuery('#video').prop('src', videoURL);
                     jQuery('#circle').text('Close');
                 } else {
                     stopVideo();
                 }
             });
             $("#close-btn").on("click", function() {
                 stopVideo();
             });
             hoverUnhideHide('#playVideo', '#circle');
             hoverHideUnhide('#video-wrapper iframe', '#circle');
             hoverHideUnhide('#close-btn', '#circle');

             function stopVideo() {
                 $('#video').prop('src', '');
                 $('#circle').text('Play');
                 $("#lightbox").fadeOut(500);
             };

             function youtube_parser(url) {
                 var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#&?]*).*/;
                 var match = url.match(regExp);
                 return (match && match[7].length == 11) ? match[7] : false;
             }
         });

         gsap.set("#circle", {
             xPercent: 0,
             yPercent: 0
         });

         let xTo = gsap.quickTo("#circle", "x", {
                 duration: 0.6,
                 ease: "power3"
             }),
             yTo = gsap.quickTo("#circle", "y", {
                 duration: 0.6,
                 ease: "power3"
             });

         document.getElementById('playVideo').addEventListener("mousemove", e => {
             xTo(e.clientX);
             yTo(e.clientY);
         });
     </script>

